import React, { useState } from 'react';
import { Box, Container, Grid } from '@mui/material';
import { Route, Link, Routes } from 'react-router-dom';
import styled, { keyframes } from 'styled-components';
import ResponsiveAppBar from './appBar/navbar';
import SignInForm from './signIn_form/signIn';
import Join from './JoinForm/join';
import './test.css'

const GlassDiv = styled.div`
  position: absolute;
  top: ${(props) => props.btnPosition.top};
  left: ${(props) => props.btnPosition.left};
  z-index: 2;
  width: 0;
  height: 0;
  padding: 20px;
  box-sizing: border-box; 
  background: rgba(241, 241, 241, 0.2);
  backdrop-filter: blur(10px);
  animation: ${(props) => slideInFromTopRight(props.btnPosition)} 0.5s ease-out forwards;
`;

const slideInFromTopRight = (btnPosition) => keyframes`
   0% {
     transform: translate3d(${btnPosition.x}px, ${btnPosition.y}px, 0);
     width: 0;
     height: 0;
   }
   100% {
     transform: translate3d(0, 0, 0);
     width: 100%;
     height: 100%;
   }
`;

const scrollFromTop = keyframes`
  0% {
    transform: translateY(-100%);
  }
  100% {
    transform: translateY(0);
  }
`;

const AnimatedJoin = styled(Join)`
   animation: ${scrollFromTop} 3s ease-out forwards;
    animation-delay: 0.5s;
`;

export default function Home() {
  const [joinNowBtnTrigger, setJoinNowBtnTrigger] = useState(false);
  const [btnPosition, setBtnPosition] = useState();

  const handleJoinNowClick = (ref) => {
    console.log('from home : ', ref.getBoundingClientRect());
    setBtnPosition( ref.getBoundingClientRect());
    setJoinNowBtnTrigger(true);
  };

  const timeOut = () => {
    setTimeout(() => {
        return <AnimatedJoin />
      }, 500); // Change this value to adjust the delay
  }

  React.useEffect(() => {
  }, [btnPosition]);

  return (
    <>
      {joinNowBtnTrigger && (
        <GlassDiv btnPosition={btnPosition}>
          {timeOut()}
        </GlassDiv>
      )}
      <Container maxWidth="xl" sx={{ pt: '10px' }}>
        <Box sx={{ width: '80%', mx: 'auto', flexGrow: 1 }}>
          <Grid container spacing={1}>
            <Grid item xs={12} md={12}>
              <ResponsiveAppBar JoinNowCallBack={handleJoinNowClick} />
            </Grid>
            <Grid item xs={12} md={12}>
                {/* <div className="circle-background"></div> */}
              <SignInForm />
            </Grid>
          </Grid>
        </Box>
      </Container>
    </>
  );
}
