import React, { useState } from 'react';
import { Box, Container, Grid } from '@mui/material';
import { Route, Link, Routes } from 'react-router-dom';
import styled, { keyframes } from 'styled-components';
import ResponsiveAppBar from './appBar/navbar';
import SignInForm from './signIn_form/signIn';
import Join from './JoinForm/join';

const slideInFromPosition = keyframes`
  0% {
    transform: translateY(0) translateX(0);
  }
  100% {
    transform: translateY(0) translateX(0);
  }
`;

const GlassDiv = styled.div`
  position: absolute;
  top: ${(props) => props.position.top}px;
  left: ${(props) => props.position.left}px;
  z-index: 2;
  width: 100%;
  height: 100%;
  padding: 20px;
  background: rgba(210, 210, 210, 0.4);
  backdrop-filter: blur(10px);
  animation: ${slideInFromPosition} 1s ease-out;
`;

export default function Home() {
    const [joinNowBtnTrigger, setJoinNowBtnTrigger] = useState(false);
    const [joinNowBtnPosition, setJoinNowBtnPosition] = useState(null);

    const handleJoinNowClick = (joinNowButtonRef) => {
        setJoinNowBtnTrigger(true);
        setJoinNowBtnPosition(joinNowButtonRef.getBoundingClientRect());
    };

    return (
        <>
            {joinNowBtnTrigger && joinNowBtnPosition && (
                <GlassDiv position={joinNowBtnPosition}>
                    <Join />
                </GlassDiv>
            )}
            <Container maxWidth="xl" sx={{ pt: '10px' }}>
                <Box sx={{ width: '80%', mx: 'auto', flexGrow: 1 }}>
                    <Grid container spacing={1}>
                        <Grid item xs={12} md={12}>
                            <ResponsiveAppBar JoinNowCallBack={handleJoinNowClick} />
                        </Grid>
                        <Grid item xs={12} md={12}>
                            <SignInForm />
                        </Grid>
                    </Grid>
                </Box>
            </Container>
        </>
    );
}
