import React, { useEffect } from 'react';

function FacebookLoginButton({ appId, apiVersion, onLogin, onFailure }) {
    useEffect(() => {
        // Load the Facebook SDK asynchronously
        (function (d, s, id) {
            var js, fjs = d.getElementsByTagName(s)[0];
            if (d.getElementById(id)) return;
            js = d.createElement(s); js.id = id;
            js.src = "https://connect.facebook.net/en_US/sdk.js";
            fjs.parentNode.insertBefore(js, fjs);
        }(document, 'script', 'facebook-jssdk'));

        // Initialize the Facebook SDK
        window.fbAsyncInit = function () {
            FB.init({
                appId: '3373040909602735',
                cookie: true,
                xfbml: true,
                version: apiVersion
            });

            // Check if the user is already logged in
            FB.getLoginStatus(function (response) {
                if (response.status === 'connected') {
                    onLogin(response.authResponse);
                }
            });
        };
    }, [appId, apiVersion, onLogin, onFailure]);

    function handleLoginClick() {
        FB.login(function (response) {
            if (response.authResponse) {
                onLogin(response.authResponse);
            } else {
                onFailure();
            }
        }, { scope: 'public_profile,email' });
    }

    return (
        <button onClick={handleLoginClick}>Login with Facebook</button>
    );
}

export default FacebookLoginButton;
